import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { environment } from 'src/environments/environment';
import { AngularFireAuth } from '@angular/fire/compat/auth';
import firebase from 'firebase/compat/app';

@Injectable({
  providedIn: 'root'
})
export class StationRestService {
  httpOptions = new HttpHeaders().set('Content-Type', 'application/json');

  constructor(
    private http: HttpClient,
    private fireAuth: AngularFireAuth
    ) { }

  createStation(params:{}){
    return this.http.post(environment.baseUri + 'station/createStation', params, {headers: this.httpOptions});
  }

  getVolunteerStationsF(){
    return this.http.get(environment.baseUri + 'station/getVolunteerStationsF', {headers: this.httpOptions})
  }

  getMunicipalStationsF(){
    return this.http.get(environment.baseUri + 'station/getMunicipalStationsF', {headers: this.httpOptions})
  }

  getFireStations(){
    return this.http.get(environment.baseUri + 'station/getFireStations', {headers: this.httpOptions})
  }

  getPoliceStations(){
    return this.http.get(environment.baseUri + 'station/getPoliceStations', {headers: this.httpOptions})
  }

  getNationalStationsP(){
    return this.http.get(environment.baseUri + 'station/getNationalStationsP', {headers: this.httpOptions})
  }

  getMunicipalStationsP(){
    return this.http.get(environment.baseUri + 'station/getMunicipalStationsP', {headers: this.httpOptions})
  }

  updateStation
  async loginGoogle(){
    try{
      this.fireAuth.setPersistence('none');
      return await this.fireAuth.signInWithPopup(new firebase.auth.GoogleAuthProvider());
    }catch(err){
      return console.log(err);
    }
  }
}
